INSERT INTO `wp_4_postmeta` VALUES (1,2,'_wp_page_template','default'),(4,5,'_wp_page_template','default'),(5,12,'Box 4 Icon','http://www.topcoder.com/asteroids/files/2013/08/iconHv.png'),(6,12,'Banner Image','http://tcrobots.wpengine.com/wp-content/uploads/2014/11/Astrobee-6.png'),(7,12,'Box 1 Icon','http://www.topcoder.com/asteroids/files/2013/08/iconNew.png'),(8,12,'Box 2 Icon','http://www.topcoder.com/asteroids/files/2013/08/iconNew.png'),(9,12,'Box 3 Icon','http://www.topcoder.com/asteroids/files/2013/08/iconNTL.png'),(10,12,'Box 4 Text','Harvard Business School in association with Harvard\'s Institute of Quantitative Social Science are strategic participants in NASA\'s Center of Excellence for Collaborative Innovation and the NASA Tournament Lab. <a href=\"http://projects.iq.harvard.edu/nasatournamentlab/\">Learn More</a>'),(11,12,'Box 4 Title','Harvard University'),(12,12,'Video Box Text','A simple introduction to the Asteroid Grand Challenge and the unique aspects of this challenge.'),(13,12,'Video Box Title','Challenge Overview Video'),(14,12,'Box3 URL','http://community.topcoder.com/ntl/about/'),(15,12,'Box3 Text','Harvard University\nHarvard Business School in association with Harvard\'s Institute of Quantitative Social Sciences are strategic participants in NASA\'s Center of Excellence for Collaborative Innovation and the NASA Tournament Lab.'),(16,12,'Box2 Text','NASA Tournament Lab\nNASA, Harvard Business School in association with Harvard\'s Institute of Quantitative Social Sciences, and TopCoder have established the NASA Tournament Lab (NTL), which will enable the TopCoder community to compete amongst each other to create the most innovative, most efficient, and most optimized solutions for specific, real-world challenges being faced by NASA researchers.'),(17,12,'Video Title','NASA Tournament Lab'),(18,12,'Box2 URL','http://community.topcoder.com/ntl'),(19,12,'Box1 URL','http://www.topcoder.com/reg'),(20,12,'_edit_last','3'),(21,12,'_wp_page_template','home.php'),(22,12,'Challenge Banner Text','<h2>NASA\'s Free Flying Robot Challenge</h2>\n<div><para>\n<br /><br />\nWelcome to the NASA Free Flying Robot Mission Patch & Naming Challenge sponsored by the NASA Tournament Lab! \n<br /><br />\nIn October, 2014, NASA announced a challenge to develop a name and design a mission patch for a \"Free Flying Robot\" that will be used on-board the International Space Station (ISS) in 2017. The robot was previously called the “Free Flying Robot”. Not that catchy, right?\n<br /><br />\nAnd that\'s where NASA needed help from the Topcoder community - to help design a custom mission patch AND develop a name for the Free Flying Robot.\n<br /><br />\nThe challenge was successfully completed in November, 2014. NASA opted to combine elements of the winning placements into the final mission patch - the winning name is:  <strong><a href=\"http://tcrobots.wpengine.com/wp-content/uploads/2014/11/Astrobee-Small.png\">Astrobee</a></strong>\n<br /><br />\nOver 818 folks registered for the challenge, with 83 checkpoint submissions and 30 final submissions.\n<br /><br />\nFor more information about the FFR program, check out <strong><a href=\"http://www.topcoder.com/blog/name-nasas-new-free-flying-robot/\">our blog</a></strong> for more info, and a cool \"user scenario\".\n<br /><br />\n</para>\n</div>\n\n'),(23,12,'Box1 Text','New to TopCoder?\nTopCoder is the world\'s largest platform for digital open innovation. We provide the platform and community of over 500,000 global members to accelerate the development of new digital products and services for our clients.'),(24,12,'Box 2 Text','<a href=\"http://topcoder.com\">topcoder</a> is the world\'s largest platform for digital open innovation. We provide the platform and community of over 600,000 global members to accelerate the development of new digital products and services for our clients. <a href=\"http://www.topcoder.com/?action=callback#\">Register Now</a>'),(25,12,'Box 1 Text','A simple introduction to the Asteroid Grand Challenge and its unique aspects.'),(26,12,'Box 3 Text','NASA, Harvard Business School in association with Harvard\'s Institute of Quantitative Social Science, and topcoder have established the NASA Tournament Lab (NTL), enabling the topcoder community to compete to create the most innovative, efficient and optimized solutions for specific real-world challenges being faced by NASA researchers. <a href=\"http://www.nasa.gov/coeci/ntl\">Learn More</a>'),(27,12,'Box 3 Title','NASA Tournament Lab'),(28,12,'Box 2 Title','New to topcoder?'),(29,12,'Box 1 Title','Challenge Overview Video'),(30,12,'Video YouTube ID','xki5Q_LRfeg'),(31,12,'open_graph_title',''),(32,12,'open_graph_description',''),(33,12,'open_graph_type','blog'),(34,12,'open_graph_image',''),(35,12,'twitter_cards_post_description',''),(36,12,'twitter_cards_post_title',''),(37,12,'twitter_cards_post_creator_username',''),(38,12,'twitter_cards_post_type','summary'),(39,12,'twitter_cards_post_image',''),(40,12,'twitter_cards_post_iframe',''),(41,12,'twitter_cards_post_stream',''),(42,19,'_menu_item_type','custom'),(43,19,'_menu_item_menu_item_parent','0'),(44,19,'_menu_item_object_id','19'),(45,19,'_menu_item_object','custom'),(46,19,'_menu_item_target',''),(47,19,'_menu_item_classes','a:1:{i:0;s:8:\"iconBlog\";}'),(48,19,'_menu_item_xfn',''),(49,19,'_menu_item_url','http://www.topcoder.com/blog'),(50,20,'_menu_item_type','custom'),(51,20,'_menu_item_menu_item_parent','0'),(52,20,'_menu_item_object_id','20'),(53,20,'_menu_item_object','custom'),(54,20,'_menu_item_target',''),(55,20,'_menu_item_classes','a:1:{i:0;s:11:\"iconYoutube\";}'),(56,20,'_menu_item_xfn',''),(57,20,'_menu_item_url','http://www.youtube.com/topcoderinc'),(58,21,'_menu_item_type','custom'),(59,21,'_menu_item_menu_item_parent','0'),(60,21,'_menu_item_object_id','21'),(61,21,'_menu_item_object','custom'),(62,21,'_menu_item_target',''),(63,21,'_menu_item_classes','a:1:{i:0;s:12:\"iconFacebook\";}'),(64,21